import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './AdminPanel.css';

function AdminPanel() {
  const navigate = useNavigate();
  const [sessions, setSessions] = useState([]);
  const [showSessions, setShowSessions] = useState(false); // Oturumları listelemek için
  const [session, setSession] = useState({
    title: '',
    date: '',
    type: '',
    speaker: '',
    description: '',
    code: '',
    startTime: '',
    endTime: '',
    location: '',
    sessionChair: '',
  });

  // Oturumları API'den çek
  useEffect(() => {
    const fetchSessions = async () => {
      try {
        const response = await fetch('http://localhost:4000/api/sessions/list');
        const data = await response.json();
        setSessions(data);
      } catch (error) {
        console.error('Oturumlar alınırken hata oluştu:', error);
      }
    };

    fetchSessions();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSession({ ...session, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      ...session,
      speaker: session.speaker || '',
      code: session.code || '',
      sessionChair: session.sessionChair || '',
    };

    try {
      const response = await fetch('http://localhost:4000/api/sessions/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        alert('Oturum başarıyla eklendi!');
        setSession({
          title: '',
          date: '',
          type: '',
          speaker: '',
          description: '',
          code: '',
          startTime: '',
          endTime: '',
          location: '',
          sessionChair: '',
        });
        setShowSessions(false); // Yeni ekleme sonrası listeyi gizle
      } else {
        const errorData = await response.json();
        console.error('Hata:', errorData.error);
        alert('Oturum eklenirken bir hata oluştu!');
      }
    } catch (error) {
      console.error('Hata:', error);
      alert('Sunucuya bağlanılamadı.');
    }
  };

  const handleDelete = async (id) => {
    try {
      await fetch(`http://localhost:4000/api/sessions/${id}`, {
        method: 'DELETE',
      });
      setSessions(sessions.filter((session) => session._id !== id)); // Listeden sil
      alert('Oturum başarıyla silindi!');
    } catch (error) {
      console.error('Oturum silme sırasında hata:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Token'ı kaldır
    navigate('/admin/login'); // Admin Login sayfasına yönlendirme
  };

  return (
    <div className="admin-panel-container">
      <div className="header">
        <button onClick={handleLogout} className="logout-button">
          Çıkış Yap
        </button>
      </div>

      <button
        onClick={() => setShowSessions(!showSessions)}
        className="view-sessions-button"
      >
        {showSessions ? 'Kapat' : 'Oturumları Görüntüle'}
      </button>

      {showSessions && (
        <div className="session-list">
          <h2>Mevcut Oturumlar</h2>
          {sessions.map((session) => (
            <div key={session._id} className="session-item">
              <h3>{session.title}</h3>
              <button onClick={() => alert(`Güncelle: ${session._id}`)}>
                Güncelle
              </button>
              <button onClick={() => handleDelete(session._id)}>Sil</button>
            </div>
          ))}
        </div>
      )}

      {!showSessions && (
        <div className="form-container">
          <h1>Oturum Ekle</h1>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="title">Oturum Başlığı</label>
              <input
                type="text"
                id="title"
                name="title"
                value={session.title}
                onChange={handleChange}
                placeholder="Başlık Girin"
              />
            </div>
            <div className="form-group">
              <label htmlFor="date">Tarih</label>
              <input
                type="date"
                id="date"
                name="date"
                value={session.date}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="type">Tür</label>
              <input
                type="text"
                id="type"
                name="type"
                value={session.type}
                onChange={handleChange}
                placeholder="Tür Girin"
              />
            </div>
            <div className="form-group">
              <label htmlFor="speaker">Konuşmacı</label>
              <input
                type="text"
                id="speaker"
                name="speaker"
                value={session.speaker}
                onChange={handleChange}
                placeholder="Konuşmacı Adı Girin"
              />
            </div>
            <div className="form-group">
              <label htmlFor="description">Açıklama</label>
              <textarea
                id="description"
                name="description"
                value={session.description}
                onChange={handleChange}
                placeholder="Açıklama Girin"
              ></textarea>
            </div>
            <div className="form-group">
              <label htmlFor="code">Oturum Kodu</label>
              <input
                type="text"
                id="code"
                name="code"
                value={session.code}
                onChange={handleChange}
                placeholder="Oturum Kodu Girin"
              />
            </div>
            <div className="time-group">
              <div>
                <label htmlFor="startTime">Başlama Saati</label>
                <input
                  type="time"
                  id="startTime"
                  name="startTime"
                  value={session.startTime}
                  onChange={handleChange}
                />
              </div>
              <div>
                <label htmlFor="endTime">Bitiş Saati</label>
                <input
                  type="time"
                  id="endTime"
                  name="endTime"
                  value={session.endTime}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="location">Yer</label>
              <input
                type="text"
                id="location"
                name="location"
                value={session.location}
                onChange={handleChange}
                placeholder="Yer Girin"
              />
            </div>
            <div className="form-group">
              <label htmlFor="sessionChair">Oturum Başkanı</label>
              <input
                type="text"
                id="sessionChair"
                name="sessionChair"
                value={session.sessionChair}
                onChange={handleChange}
                placeholder="Oturum Başkanı Girin"
              />
            </div>
            <button type="submit" className="submit-button">
              Oturumu Kaydet
            </button>
          </form>
        </div>
      )}
    </div>
  );
}

export default AdminPanel;
