import React, { useState, useEffect } from 'react';
import api from '../services/api';
import './SessionList.css';

function SessionList() {
  // Filtre durumu için useState tanımlayın
  const [filter, setFilter] = useState({ date: '', type: '', manager: '' });
  const [sessions, setSessions] = useState([]);

  useEffect(() => {
    const fetchSessions = async () => {
      try {
        const response = await api.get('/sessions/list');
        setSessions(response.data);
      } catch (error) {
        console.error('Error fetching sessions:', error);
      }
    };

    fetchSessions();
  }, []);
  const handleFilterChange = async (e) => {
    const { name, value } = e.target;
  
    // Eğer tarih filtreleniyorsa, tarihi ISO formatına çevir
    const formattedValue = name === 'date' && value ? new Date(value).toISOString().split('T')[0] : value;
  
    // Filtreyi güncelle
    setFilter({ ...filter, [name]: formattedValue });
  
    try {
      // Filtreleme için backend'e istek gönder
      const response = await api.get('/sessions/list', {
        params: { [name]: formattedValue },
      });
      setSessions(response.data); // Filtrelenmiş oturumları güncelle
    } catch (error) {
      console.error('Error filtering sessions:', error);
    }
  };
  
  const filteredSessions = sessions.filter((session) => {
    return (
      (filter.type ? session.type === filter.type : true) &&
      (filter.date ? session.date === filter.date : true) &&
      (filter.manager ? session.sessionChair === filter.manager : true)
    );
  });
  

  return (
    <div className="session-list-container">
      <h1>Oturum Türleri</h1>

      {!selectedType ? (
        // Tür Kartlarını Göster
        <div className="group-cards">
          {Object.keys(groupedByType).map((type) => (
            <div
              key={type}
              className="group-card"
              onClick={() => setSelectedType(type)}
            >
              <h2>{type}</h2>
            </div>
          ))}
        </div>
      ) : (
        // Seçilen Türe Ait Oturumları Göster
        <div>
          <button onClick={() => setSelectedType(null)}>Geri</button>
          <h2>{selectedType} Oturumları</h2>

          {/* Filtreleme Seçenekleri */}
          <div className="filter-container">
            <input
              type="date"
              name="date"
              value={filters.date}
              onChange={handleFilterChange}
            />
            <input
              type="text"
              name="sessionChair"
              value={filters.sessionChair}
              onChange={handleFilterChange}
              placeholder="Oturum Başkanı Girin"
            />
          </div>

          <div className="session-cards">
            {filteredSessions.map((session) => (
              <div key={session._id} className="session-card">
                <h3>{session.title}</h3>
                <p><strong>Tarih:</strong> {new Date(session.date).toLocaleDateString()}</p>
                <p><strong>Konu:</strong> {session.code}: {session.description}</p>
                <p><strong>Konuşmacı:</strong> {session.speaker}</p>
                <p><strong>Zaman:</strong> {session.startTime} - {session.endTime}</p>
                <p><strong>Yer:</strong> {session.location}</p>
                <p><strong>Oturum Başkanı:</strong> {session.sessionChair}</p>
                <button onClick={() => window.open(`/pdfs/${session._id}.pdf`)}>
                  PDF Değerlendirme Formu İndir
                </button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default SessionList;
