import React, { useState, useEffect } from 'react';
import api from '../services/api';
import './SessionList.css';

function SessionList() {
  const [sessions, setSessions] = useState([]);
  const [groupedByType, setGroupedByType] = useState({});
  const [selectedType, setSelectedType] = useState(null);
  const [filters, setFilters] = useState({ date: '', sessionChair: '', startTime: '' });
  const [availableStartTimes, setAvailableStartTimes] = useState([]); // Başlangıç saatlerini tutacak state

  // Tüm oturumları API'den çek
  useEffect(() => {
    const fetchSessions = async () => {
      try {
        const response = await api.get('/sessions/list');
        const data = response.data;

        // Türlere göre gruplama
        const grouped = data.reduce((acc, session) => {
          acc[session.type] = acc[session.type] || [];
          acc[session.type].push(session);
          return acc;
        }, {});
        setGroupedByType(grouped);
      } catch (error) {
        console.error('Oturumlar alınırken hata oluştu:', error);
      }
    };

    fetchSessions();
  }, []);

  // Tarihe göre başlangıç saatlerini al
  useEffect(() => {
    const fetchAvailableStartTimes = async () => {
      try {
        const response = await api.get('/sessions/list', {
          params: { date: filters.date },
        });

        const times = [...new Set(response.data.map((session) => session.startTime))];
        setAvailableStartTimes(times.sort());
      } catch (error) {
        console.error('Başlangıç saatleri alınırken hata oluştu:', error);
      }
    };

    if (filters.date) {
      fetchAvailableStartTimes();
    }
  }, [filters.date]);

  // Filtre değişikliklerini işleyin
  const handleFilterChange = (e) => {
    const { name, value } = e.target;

    // Tarih veya saat formatını doğrula
    const formattedValue =
        name === 'date' && value ? new Date(value).toISOString().split('T')[0] : value;

    setFilters({ ...filters, [name]: formattedValue });
};

  // PDF indirme fonksiyonu
  const handleDownloadPDF = async (sessionTitle) => {
    try {
      const response = await api.get(`/sessions/pdf/${sessionTitle}`, {
        responseType: 'blob',
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `${sessionTitle}.pdf`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
    } catch (error) {
      console.error('PDF indirilemedi:', error);
      alert('PDF indirilemedi. Sunucuya bağlanılamıyor.');
    }
  };

  // Filtrelenen oturumlar
  const filteredSessions = selectedType
    ? groupedByType[selectedType].filter((session) => {
        const isDateMatch = filters.date
          ? new Date(session.date).toISOString().split('T')[0] === filters.date
          : true;

        const isSessionChairMatch = filters.sessionChair
          ? session.sessionChair?.toLowerCase().includes(filters.sessionChair.toLowerCase())
          : true;

        const isStartTimeMatch = filters.startTime
          ? session.startTime >= filters.startTime
          : true;

        return isDateMatch && isSessionChairMatch && isStartTimeMatch;
      })
    : [];

  return (
    <div className="session-list-container">
      <h1>Uluslararası Bilgi​sayar Bilimleri ve Mühendisliği Konferansı (UBMK 2024)</h1>

      {!selectedType ? (
        <div className="group-cards">
          {Object.keys(groupedByType).map((type) => (
            <div
              key={type}
              className="group-card"
              onClick={() => {
                setSelectedType(type);
                setFilters({ date: '', sessionChair: '', startTime: '' });
              }}
            >
              <h2>{type}</h2>
            </div>
          ))}
        </div>
      ) : (
        <div>
          <button
            onClick={() => {
              setSelectedType(null);
              setFilters({ date: '', sessionChair: '', startTime: '' });
            }}
            className="back-button"
          >
            Geri
          </button>

          <h2>{selectedType} Oturumları</h2>

          <div className="filter-container">
            <label>
              Tarih:
              <input
                type="date"
                name="date"
                value={filters.date}
                onChange={handleFilterChange}
              />
            </label>
            <label>
              Oturum Başkanı:
              <input
                type="text"
                name="sessionChair"
                value={filters.sessionChair}
                onChange={handleFilterChange}
                placeholder="Oturum Başkanı Girin"
              />
            </label>
            <label>
              Başlama Saati:
              <select
                name="startTime"
                value={filters.startTime}
                onChange={handleFilterChange}
              >
                <option value="">Saat Seçin</option>
                {availableStartTimes.map((time) => (
                  <option key={time} value={time}>
                    {time}
                  </option>
                ))}
              </select>
            </label>
          </div>

          <div className="session-cards">
            {filteredSessions.map((session) => (
              <div key={session._id} className="session-card">
                <h3>{session.title}</h3>
                <p><strong>Tarih:</strong> {new Date(session.date).toLocaleDateString()}</p>
                <p><strong>Konu:</strong> {session.code}: {session.description}</p>
                <p><strong>Konuşmacı:</strong> {session.speaker}</p>
                <p><strong>Zaman:</strong> {session.startTime} - {session.endTime}</p>
                <p><strong>Yer:</strong> {session.location}</p>
                <p><strong>Oturum Başkanı:</strong> {session.sessionChair}</p>
                <button onClick={() => handleDownloadPDF(session.title)}>
                  PDF İndir
                </button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default SessionList;
