import React, { useState } from 'react';
import api from '../services/api';

function AdminPanel() {
  const [title, setTitle] = useState('');
  const [date, setDate] = useState('');
  const [type, setType] = useState('');
  const [speaker, setSpeaker] = useState('');
  const [description, setDescription] = useState('');
  const [code, setCode] = useState(''); // Oturum Kodu
  const [time, setTime] = useState(''); // Zaman
  const [location, setLocation] = useState(''); // Yer
  const [sessionChair, setSessionChair] = useState(''); // Oturum Başkanı

  const handleAddSession = async () => {
    try {
      await api.post('/sessions/add', {
        title,
        date,
        type,
        speaker,
        description,
        code,
        time,
        location,
        sessionChair
      });
      alert('Oturum başarıyla eklendi!');
      // Formu sıfırla
      setTitle('');
      setDate('');
      setType('');
      setSpeaker('');
      setDescription('');
      setCode('');
      setTime('');
      setLocation('');
      setSessionChair('');
    } catch (err) {
      console.error(err);
      alert('Oturum eklenirken bir hata oluştu!');
    }
  };

  return (
    <div>
      <h2>Yeni Oturum Ekle</h2>
      <input
        type="text"
        placeholder="Başlık"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
      />
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />
      <input
        type="text"
        placeholder="Tür"
        value={type}
        onChange={(e) => setType(e.target.value)}
      />
      <input
        type="text"
        placeholder="Konuşmacı"
        value={speaker}
        onChange={(e) => setSpeaker(e.target.value)}
      />
      <input
        type="text"
        placeholder="Oturum Kodu"
        value={code}
        onChange={(e) => setCode(e.target.value)}
      />
      <input
        type="text"
        placeholder="Zaman (Örn: 09:30-10:00)"
        value={time}
        onChange={(e) => setTime(e.target.value)}
      />
      <input
        type="text"
        placeholder="Yer"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />
      <input
        type="text"
        placeholder="Oturum Başkanı"
        value={sessionChair}
        onChange={(e) => setSessionChair(e.target.value)}
      />
      <textarea
        placeholder="Açıklama"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button onClick={handleAddSession}>Ekle</button>
    </div>
  );
}

export default AdminPanel;
