import React from 'react';
import api from '../services/api';

function SessionList({ sessions }) {
    const handleDownloadPDF = async (id, title) => {
        try {
            const response = await api.get(`/sessions/pdf/${id}`, {
                responseType: 'blob',
            });

            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', `${title}.pdf`);
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
        } catch (error) {
            console.error('PDF indirilemedi:', error);
            alert('PDF indirilemedi. Sunucuya bağlanılamıyor.');
        }
    };

    return (
        <div>
            <h2 style={{ textAlign: 'center' }}>Oturum Listesi</h2>
            <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
                gap: '20px',
                padding: '20px'
            }}>
                {sessions.map((session) => (
                    <div key={session._id} style={{
                        border: '1px solid #ddd',
                        borderRadius: '8px',
                        padding: '20px',
                        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                        backgroundColor: '#fff'
                    }}>
                        <h3 style={{ marginBottom: '10px' }}>{session.title}</h3>
                        <p><strong>Tarih:</strong> {session.date}</p>
                        <p><strong>Tür:</strong> {session.type}</p>
                        <p><strong>Konuşmacı:</strong> {session.speaker}</p>
                        <p><strong>Zaman:</strong> {session.startTime} - {session.endTime}</p>
                        <p><strong>Yer:</strong> {session.location}</p>
                        <p><strong>Oturum Başkanı:</strong> {session.sessionChair}</p>
                        <p><strong>Konu:</strong> {session.description} ({session.code})</p>
                        <button
                            onClick={() => handleDownloadPDF(session._id, session.title)}
                            style={{
                                marginTop: '10px',
                                backgroundColor: '#007bff',
                                color: '#fff',
                                border: 'none',
                                padding: '10px 15px',
                                borderRadius: '5px',
                                cursor: 'pointer'
                            }}
                        >
                            PDF Değerlendirme Formu İndir
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default SessionList;
