import React, { useState, useEffect } from 'react';
import api from '../services/api';
import './SessionList.css';

function SessionList() {
  const [sessions, setSessions] = useState([]);
  const [selectedSessionCode, setSelectedSessionCode] = useState(null); // Seçilen sessionCode

  useEffect(() => {
    const fetchSessions = async () => {
      try {
        const response = await api.get('/sessions/list');
        setSessions(response.data);
      } catch (error) {
        console.error('Error fetching sessions:', error);
      }
    };

    fetchSessions();
  }, []);

  // Oturumları gruplamak için
  const groupedSessions = sessions.reduce((groups, session) => {
    const groupKey = session.code; // Oturum kodu
    if (!groups[groupKey]) {
      groups[groupKey] = [];
    }
    groups[groupKey].push(session);
    return groups;
  }, {});

  const handleSessionClick = (code) => {
    setSelectedSessionCode(code); // Seçilen kodu ayarla
  };

  return (
    <div className="session-list-container">
      <h1>Oturumlar</h1>

      {selectedSessionCode === null ? (
        // Gruplanmış oturumları göster
        <div className="session-groups">
          {Object.keys(groupedSessions).map((code) => (
            <div
              key={code}
              className="session-group-card"
              onClick={() => handleSessionClick(code)}
            >
              <h3>{code}</h3>
              <p>{groupedSessions[code][0]?.description || 'Açıklama Yok'}</p>
            </div>
          ))}
        </div>
      ) : (
        // Seçilen gruptaki oturumları göster
        <div className="session-cards">
          <button onClick={() => setSelectedSessionCode(null)}>Geri</button>
          {groupedSessions[selectedSessionCode].map((session) => (
            <div key={session._id} className="session-card">
              <h3>{session.title}</h3>
              <p><strong>Tarih:</strong> {new Date(session.date).toLocaleDateString()}</p>
              <p><strong>Tür:</strong> {session.type}</p>
              <p><strong>Konu:</strong> {session.description}</p>
              <p><strong>Konuşmacı:</strong> {session.speaker}</p>
              <p><strong>Yer:</strong> {session.location}</p>
              <button onClick={() => window.open(`/pdfs/${session._id}.pdf`)}>
                PDF Değerlendirme Formu İndir
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default SessionList;
