import React, { useState, useEffect } from 'react';
import api from '../services/api';
import './SessionList.css';

function SessionList() {
  const [sessions, setSessions] = useState([]);
  const [groupedSessions, setGroupedSessions] = useState({});
  const [selectedSession, setSelectedSession] = useState(null);

  useEffect(() => {
    const fetchSessions = async () => {
      try {
        const response = await api.get('/sessions/list');
        const data = response.data;

        // Veriyi SESSION başlıklarına göre gruplama
        const grouped = data.reduce((acc, session) => {
          acc[session.title] = acc[session.title] || [];
          acc[session.title].push(session);
          return acc;
        }, {});
        setGroupedSessions(grouped);
      } catch (error) {
        console.error('Error fetching sessions:', error);
      }
    };

    fetchSessions();
  }, []);

  return (
    <div className="session-list-container">
      <h1>Oturumlar</h1>
      
      {!selectedSession ? (
        // Grupları Göster
        <div className="group-cards">
          {Object.keys(groupedSessions).map((groupTitle) => (
            <div
              key={groupTitle}
              className="group-card"
              onClick={() => setSelectedSession(groupTitle)}
            >
              <h2>{groupTitle}</h2>
            </div>
          ))}
        </div>
      ) : (
        // Seçilen Oturumu Göster
        <div>
          <button onClick={() => setSelectedSession(null)}>Geri</button>
          <h2>{selectedSession}</h2>
          <div className="session-cards">
            {groupedSessions[selectedSession].map((session) => (
              <div key={session._id} className="session-card">
                <h3>{session.title}</h3>
                <p><strong>Tarih:</strong> {new Date(session.date).toLocaleDateString()}</p>
                <p><strong>Tür:</strong> {session.type}</p>
                <p><strong>Konu:</strong> {session.code}: {session.description}</p>
                <p><strong>Konuşmacı:</strong> {session.speaker}</p>
                <p><strong>Zaman:</strong> {session.startTime} - {session.endTime}</p>
                <p><strong>Yer:</strong> {session.location}</p>
                <p><strong>Oturum Başkanı:</strong> {session.sessionChair}</p>
                <button onClick={() => window.open(`/pdfs/${session._id}.pdf`)}>
                  PDF Değerlendirme Formu İndir
                </button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default SessionList;
