import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link, useLocation } from 'react-router-dom';
import AdminLogin from './components/AdminLogin';
import SessionList from './components/SessionList';
import AdminPanel from './components/AdminPanel';

function App() {
  const location = useLocation(); // Şu anki rota bilgisi

  return (
    <div className="app-container">
      {/* Eğer rota "/" ise Admin Girişi butonunu göster */}
      {location.pathname === '/' && (
        <div className="admin-login-container">
          <Link to="/admin/login" className="admin-login-button">
            Admin Girişi
          </Link>
        </div>
      )}

      <Routes>
        {/* Ana sayfa oturum listesi */}
        <Route path="/" element={<SessionList />} />

        {/* Admin Giriş Sayfası */}
        <Route path="/admin/login" element={<AdminLogin />} />

        {/* Admin Paneli */}
        <Route path="/admin/panel" element={<AdminPanel />} />

        {/* /admin yoluna girildiğinde /admin/login'e yönlendir */}
        <Route path="/admin" element={<Navigate to="/admin/login" />} />

        {/* Hatalı rota için */}
        <Route path="*" element={<h1>404 - Sayfa Bulunamadı</h1>} />
      </Routes>
    </div>
  );
}

export default App;
