import React, { useState } from 'react';
import api from '../services/api';

function AdminPanel() {
  const [formData, setFormData] = useState({
    title: '',
    date: '',
    type: '',
    speaker: '',
    description: '',
    code: '',
    startTime: '',
    endTime: '',
    location: '',
    sessionChair: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };
  const handleAddSession = async () => {
    try {
      await api.post('/sessions/add', {
        title,
        date,
        type,
        speaker,
        description,
        code,
        startTime, // Başlangıç saati
        endTime,   // Bitiş saati
        location,
        sessionChair
      });
      alert('Oturum başarıyla eklendi!');
    } catch (err) {
      console.error(err);
      alert('Oturum eklenirken bir hata oluştu!');
    }
};

  return (
    <div>
      <h2>Yeni Oturum Ekle</h2>
      <form>
        <input
          type="text"
          name="title"
          placeholder="Başlık"
          value={formData.title}
          onChange={handleChange}
        />
        <input
          type="date"
          name="date"
          value={formData.date}
          onChange={handleChange}
        />
        <input
          type="text"
          name="type"
          placeholder="Tür"
          value={formData.type}
          onChange={handleChange}
        />
        <input
          type="text"
          name="speaker"
          placeholder="Konuşmacı"
          value={formData.speaker}
          onChange={handleChange}
        />
        <input
          type="text"
          name="code"
          placeholder="Oturum Kodu"
          value={formData.code}
          onChange={handleChange}
        />
        <input
          type="time"
          name="startTime"
          value={formData.startTime}
          onChange={handleChange}
        />
        <input
          type="time"
          name="endTime"
          value={formData.endTime}
          onChange={handleChange}
        />
        <input
          type="text"
          name="location"
          placeholder="Yer"
          value={formData.location}
          onChange={handleChange}
        />
        <input
          type="text"
          name="sessionChair"
          placeholder="Oturum Başkanı"
          value={formData.sessionChair}
          onChange={handleChange}
        />
        <textarea
          name="description"
          placeholder="Açıklama"
          value={formData.description}
          onChange={handleChange}
        />
        <button type="button" onClick={handleAddSession}>Ekle</button>
      </form>
    </div>
  );
}

export default AdminPanel;
